mapper.update:
update board_cafe_comment
set deleted='yes'
where num=#{num}

update board_cafe_comment
set content=#{content}
where num=#{num}

mapper.select:
select num, writer, content, ref_group, comment_group, deleted, regdate
from board_cafe_comment
where num=#{num}

update:
UPDATE board_cafe_comment
SET deleted='yes'
WHERE num=#{num}

UPDATE board_cafe_comment
SET content=#{content}
WHERE num=#{num}

select:
SELECT NVL(MAX(ROWNUM), 0)
FROM board_cafe_comment
WHERE ref_group=#{ref_group}
