mapper.update:
update shop
set remainCount = remainCount-1
where num=#{num}

update client_account
set money=money-(select price from shop where num=#{num})
where id=#{id}

update client_account
set point=point+(select price*0.1 from shop where num=#{num})
where id=#{id}

mapper.select:
select price
from shop11
where num=#{num}
