mapper.update:
update shop
set remainCount = remainCount-1
where num=#{num}

update client_account
set money=money-#{price}
where id=#{id}

update client_account
set point=point + #{price}*0.1
where id=#{id}

mapper.select:
select price
from shop
where num=#{num}

